$(document).ready(function(){$(document).keydown(function(b){if(b.which==27?$("#floating_login:visible"):false){hide_login()}});$("#floating_login, #background").hide().css("display","");$("#login_link").click(function(){show_login()});$("#background, .cancel_login, #close_login_box").click(function(b){hide_login()});var a="info";a=a+"@";a=a+"telescopictext.org";$(".email_address_insert").each(function(){var b=$(this).text();if(b==""){$(this).append("<a href='mailto:"+a+"'>"+a+"</a>")}else{$(this).empty().append("<a href='mailto:"+a+"'>"+b+"</a>")}});$("#donate_btn").click(function(){$("#donate_form").submit()});$("#header, #navigation, #logged_in_user").bind("mouseenter",function(){$("#navigation, #logged_in_user").css("color","#888")}).bind("mouseleave",function(){$("#navigation, #logged_in_user").css("color","")})});function hide_login(){$("#floating_login, #floating_login_positioner").fadeOut("fast");$("#background").fadeOut("fast");$("input:not(#floating_login input)").removeAttr("disabled")}function show_login(){$("#floating_login, #floating_login_positioner").fadeIn("fast");$("#background").show().fadeTo(0,0).fadeTo(150,0.6);$("#floating_login input#jsEmailInput").focus();$("input:not(#floating_login input)").attr("disabled","disabled")};